package com.hsbc.pack;

public class ThirdProgramIf {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		String shirt=" Shirt";
		String color="";
		if(color=="Red")
			shirt="Red"+shirt;
		else if(color=="Blue")
			shirt="Blue"+shirt;
		else
			shirt="White"+shirt;
		System.out.println("Value is "+shirt);

	}

}
